import React, { useState } from 'react';
import { FiHeart, FiEdit2, FiTrash2, FiEye } from 'react-icons/fi';

const MovieCard = ({ movie, onEdit, onDelete, onToggleFavorite, isFavorite, showActions = true }) => {
  const [isExpanded, setIsExpanded] = useState(false);
  const defaultImage = 'https://via.placeholder.com/400x600?text=Movie+Poster';

  return (
    <div className={`bg-gray-800/50 rounded-xl overflow-hidden shadow-lg backdrop-blur-sm border border-gray-700/50 hover:shadow-blue-500/10 transition-all duration-300 ${isExpanded ? 'col-span-full' : ''}`}>
      <div className={`grid ${isExpanded ? 'grid-cols-2 gap-6' : 'grid-cols-1'}`}>
        <div className="relative group">
          <img 
            src={movie.imageUrl || movie.poster || defaultImage} 
            alt={movie.title} 
            className={`w-full ${isExpanded ? 'h-[500px]' : 'h-64'} object-cover transition-transform duration-300 group-hover:scale-105`}
            onError={(e) => {
              e.target.onerror = null;
              e.target.src = defaultImage;
            }}
          />
        </div>
        
        <div className="p-6">
          <div className="flex justify-between items-start mb-4">
            <h3 className="text-xl font-bold text-white">{movie.title}</h3>
            <div className="flex items-center gap-2">
              {showActions && (
                <>
                  <button
                    onClick={() => onEdit(movie)}
                    className="text-blue-400 hover:text-blue-300 transition-colors duration-300"
                    title="Edit movie"
                  >
                    <FiEdit2 className="w-5 h-5" />
                  </button>
                  <button
                    onClick={() => onDelete(movie._id)}
                    className="text-red-400 hover:text-red-300 transition-colors duration-300"
                    title="Delete movie"
                  >
                    <FiTrash2 className="w-5 h-5" />
                  </button>
                  <button
                    onClick={() => setIsExpanded(!isExpanded)}
                    className="text-purple-400 hover:text-purple-300 transition-colors duration-300"
                    title={isExpanded ? "Show less" : "Show more"}
                  >
                    <FiEye className="w-5 h-5" />
                  </button>
                </>
              )}
              <button
                onClick={() => onToggleFavorite(movie._id)}
                className="text-red-400 hover:text-red-300 transition-colors duration-300"
                title={isFavorite ? "Remove from favorites" : "Add to favorites"}
              >
                <FiHeart className={`w-6 h-6 ${isFavorite ? 'fill-current' : ''}`} />
              </button>
            </div>
          </div>

          {isExpanded ? (
            <div className="space-y-4">
              <p className="text-gray-300">{movie.description}</p>
              <div className="flex items-center gap-2">
                <span className="font-bold text-gray-400">Genre:</span>
                <span className="text-gray-300">{movie.genre}</span>
              </div>
              <div className="flex items-center gap-2">
                <span className="font-bold text-gray-400">Year:</span>
                <span className="text-gray-300">{movie.year}</span>
              </div>
              <div className="flex items-center gap-2">
                <span className="font-bold text-gray-400">Rating:</span>
                <span className="text-gray-300">{movie.rating}/10</span>
              </div>
            </div>
          ) : (
            <>
              <p className="text-gray-400 mb-4 line-clamp-2">{movie.description}</p>
              <div className="flex items-center gap-2">
                <span className="px-3 py-1 bg-gray-700/50 rounded-full text-sm text-gray-300">
                  {movie.genre}
                </span>
                {movie.year && (
                  <span className="text-sm text-gray-500">
                    {movie.year}
                  </span>
                )}
              </div>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default MovieCard;
