import React from 'react';
import { Link } from 'react-router-dom';
import { FiFilm, FiGrid, FiShare2, FiArrowRight } from 'react-icons/fi';

const Home = () => {
  const features = [
    {
      icon: <FiFilm className="w-8 h-8" />,
      title: "Track Movies",
      description: "Keep track of all your favorite movies in one place",
      color: "from-blue-500 to-blue-600"
    },
    {
      icon: <FiGrid className="w-8 h-8" />,
      title: "Organize",
      description: "Organize your collection by genre, year, and more",
      color: "from-purple-500 to-purple-600"
    },
    {
      icon: <FiShare2 className="w-8 h-8" />,
      title: "Share",
      description: "Share your movie collection with friends",
      color: "from-pink-500 to-pink-600"
    }
  ];

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 via-gray-800 to-black text-white">
      <div className="container mx-auto px-4 py-20">
        <div className="text-center mb-20">
          <h1 className="text-7xl font-bold mb-8 bg-clip-text text-transparent bg-gradient-to-r from-blue-400 via-purple-400 to-pink-400 leading-tight">
            Welcome to MovieVault
          </h1>
          <p className="text-2xl text-gray-300 max-w-2xl mx-auto leading-relaxed">
            Your personal cinema collection manager. Track, organize, and share your favorite films.
          </p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-8 max-w-6xl mx-auto mb-20">
          {features.map((feature, index) => (
            <div 
              key={index}
              className="relative group"
            >
              <div className="absolute inset-0 bg-gradient-to-r opacity-0 group-hover:opacity-100 transition-opacity duration-300 rounded-2xl blur-xl"
                   style={{ background: `linear-gradient(to right, ${feature.color.split(' ')[1]}, ${feature.color.split(' ')[3]})` }}>
              </div>
              <div className="relative bg-gray-800/50 backdrop-blur p-8 rounded-2xl shadow-xl group-hover:transform group-hover:scale-105 transition-all duration-300 border border-gray-700/50">
                <div className={`bg-gradient-to-r ${feature.color} p-4 rounded-lg inline-block mb-6`}>
                  {feature.icon}
                </div>
                <h3 className="text-2xl font-bold mb-4">{feature.title}</h3>
                <p className="text-gray-300">{feature.description}</p>
              </div>
            </div>
          ))}
        </div>

        <div className="text-center space-y-8">
          <Link
            to="/signup"
            className="inline-flex items-center gap-3 bg-gradient-to-r from-blue-500 to-blue-600 text-white px-10 py-4 rounded-xl font-bold text-lg hover:from-blue-600 hover:to-blue-700 transition-all duration-300 transform hover:scale-105 shadow-lg hover:shadow-blue-500/25"
          >
            Get Started
            <FiArrowRight className="w-5 h-5" />
          </Link>
          <p className="text-gray-400">
            Already have an account?{' '}
            <Link to="/login" className="text-blue-400 hover:text-blue-300 transition-colors duration-300">
              Sign in
            </Link>
          </p>
        </div>
      </div>
    </div>
  );
};

export default Home;
