import React, { useState, useEffect } from 'react';
import { FiX, FiSave, FiFilm, FiTag, FiCalendar, FiImage, FiStar, FiFileText } from 'react-icons/fi';

const MovieForm = ({ movie, onSubmit, onCancel }) => {
  const [formData, setFormData] = useState({
    title: '',
    genre: '',
    year: '',
    poster: '',
    description: '',
    rating: 0
  });

  useEffect(() => {
    if (movie) {
      setFormData(movie);
    }
  }, [movie]);

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(formData);
  };

  const InputField = ({ icon: Icon, ...props }) => (
    <div className="relative mb-6">
      <div className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-400">
        <Icon className="w-5 h-5" />
      </div>
      <input
        {...props}
        className="w-full pl-12 pr-4 py-3 bg-gray-700/50 text-white rounded-lg border border-gray-600 focus:border-blue-500 focus:ring-2 focus:ring-blue-500/20 transition-all duration-300"
      />
    </div>
  );

  return (
    <form onSubmit={handleSubmit} className="bg-gray-800 p-8 rounded-xl shadow-xl">
      <div className="flex justify-between items-center mb-8 pb-4 border-b border-gray-700">
        <h2 className="text-2xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-blue-400 to-purple-500">
          {movie ? 'Edit Movie' : 'Add New Movie'}
        </h2>
        <button
          type="button"
          onClick={onCancel}
          className="p-2 hover:bg-gray-700 rounded-full transition-colors duration-300"
        >
          <FiX className="w-6 h-6" />
        </button>
      </div>

      <InputField
        icon={FiFilm}
        type="text"
        placeholder="Movie Title"
        value={formData.title}
        onChange={(e) => setFormData({...formData, title: e.target.value})}
        required
      />

      <InputField
        icon={FiTag}
        type="text"
        placeholder="Genre"
        value={formData.genre}
        onChange={(e) => setFormData({...formData, genre: e.target.value})}
        required
      />

      <InputField
        icon={FiCalendar}
        type="number"
        placeholder="Release Year"
        value={formData.year}
        onChange={(e) => setFormData({...formData, year: e.target.value})}
        required
      />

      <InputField
        icon={FiImage}
        type="url"
        placeholder="Poster URL"
        value={formData.poster}
        onChange={(e) => setFormData({...formData, poster: e.target.value})}
        required
      />

      <InputField
        icon={FiStar}
        type="number"
        placeholder="Rating (0-10)"
        value={formData.rating}
        onChange={(e) => setFormData({...formData, rating: e.target.value})}
        min="0"
        max="10"
      />

      <div className="relative mb-6">
        <div className="absolute left-3 top-4 text-gray-400">
          <FiFileText className="w-5 h-5" />
        </div>
        <textarea
          placeholder="Movie Description"
          value={formData.description}
          onChange={(e) => setFormData({...formData, description: e.target.value})}
          className="w-full pl-12 pr-4 py-3 bg-gray-700/50 text-white rounded-lg border border-gray-600 focus:border-blue-500 focus:ring-2 focus:ring-blue-500/20 transition-all duration-300"
          required
          rows="4"
        />
      </div>

      <div className="flex justify-end gap-4 mt-8">
        <button
          type="button"
          onClick={onCancel}
          className="px-6 py-3 bg-gray-700 rounded-lg hover:bg-gray-600 transition-colors duration-300 flex items-center gap-2"
        >
          <FiX className="w-5 h-5" />
          Cancel
        </button>
        <button
          type="submit"
          className="px-6 py-3 bg-gradient-to-r from-blue-500 to-blue-600 rounded-lg hover:from-blue-600 hover:to-blue-700 transition-all duration-300 flex items-center gap-2"
        >
          <FiSave className="w-5 h-5" />
          {movie ? 'Update Movie' : 'Add Movie'}
        </button>
      </div>
    </form>
  );
};

export default MovieForm;
