import React, { useState, useEffect } from 'react';
import { FiFilm, FiHeart, FiMail, FiUser, FiCalendar } from 'react-icons/fi';

const Profile = () => {
  const [user, setUser] = useState({
    username: localStorage.getItem('username'),
    email: localStorage.getItem('email')
  });
  const [stats, setStats] = useState({ moviesCount: 0, favoritesCount: 0 });

  useEffect(() => {
    const fetchUserProfile = async () => {
      try {
        const token = localStorage.getItem('token');
        const [userResponse, statsResponse] = await Promise.all([
          fetch('http://localhost:5000/api/auth/me', {
            headers: {
              'Authorization': `Bearer ${token}`
            }
          }),
          fetch('http://localhost:5000/api/movies/stats', {
            headers: {
              'Authorization': `Bearer ${token}`
            }
          })
        ]);

        const userData = await userResponse.json();
        const statsData = await statsResponse.json();

        setUser({
          username: userData.username || localStorage.getItem('username'),
          email: userData.email || localStorage.getItem('email')
        });

        setStats({
          moviesCount: statsData.moviesCount || 0,
          favoritesCount: statsData.favoritesCount || 0
        });
      } catch (error) {
        console.error('Error fetching profile:', error);
      }
    };

    fetchUserProfile();
  }, []);

  const displayName = user.username || user.email.split('@')[0];
  const memberSince = new Date().toLocaleDateString('en-US', {
    year: 'numeric',
    month: 'long',
    day: 'numeric'
  });

  return (
    <div className="min-h-screen bg-gradient-to-b from-gray-900 to-gray-800 text-white p-8">
      <div className="container mx-auto max-w-3xl">
        <div className="bg-gray-800 rounded-lg shadow-2xl p-8 backdrop-blur-lg bg-opacity-90">
          <div className="flex items-center gap-8 mb-12">
            <div className="relative group">
              <img
                src={`https://ui-avatars.com/api/?name=${displayName}&size=128&background=random`}
                alt="Profile"
                className="w-36 h-36 rounded-full ring-4 ring-blue-500 transition-transform duration-300 group-hover:scale-105"
              />
              <div className="absolute inset-0 rounded-full bg-blue-500 opacity-0 group-hover:opacity-20 transition-opacity duration-300"></div>
            </div>
            <div>
              <h1 className="text-4xl font-bold mb-2 text-transparent bg-clip-text bg-gradient-to-r from-blue-400 to-purple-500">
                {displayName}
              </h1>
              <div className="flex items-center text-gray-400 gap-2">
                <FiCalendar className="w-4 h-4" />
                <p>Member since {memberSince}</p>
              </div>
            </div>
          </div>

          <div className="space-y-8">
            <div className="border-t border-gray-700 pt-8">
              <h2 className="text-2xl font-semibold mb-6 flex items-center gap-2">
                <FiUser className="w-6 h-6 text-blue-400" />
                Profile Information
              </h2>
              <div className="space-y-6 bg-gray-900 p-6 rounded-lg">
                <div>
                  <label className="text-gray-400 block mb-2 flex items-center gap-2">
                    <FiUser className="w-4 h-4" />
                    Username
                  </label>
                  <div className="text-lg font-medium">{displayName}</div>
                </div>
                <div>
                  <label className="text-gray-400 block mb-2 flex items-center gap-2">
                    <FiMail className="w-4 h-4" />
                    Email
                  </label>
                  <div className="text-lg font-medium">{user.email}</div>
                </div>
              </div>
            </div>

            <div className="border-t border-gray-700 pt-8">
              <h2 className="text-2xl font-semibold mb-6">Activity Statistics</h2>
              <div className="grid grid-cols-2 gap-6">
                <div className="bg-gradient-to-br from-blue-500 to-blue-600 p-6 rounded-lg transform hover:scale-105 transition-transform duration-300">
                  <div className="flex items-center gap-3 mb-4">
                    <FiFilm className="w-6 h-6" />
                    <div className="text-3xl font-bold">{stats.moviesCount}</div>
                  </div>
                  <div className="text-gray-100">Movies Added</div>
                </div>
                <div className="bg-gradient-to-br from-purple-500 to-purple-600 p-6 rounded-lg transform hover:scale-105 transition-transform duration-300">
                  <div className="flex items-center gap-3 mb-4">
                    <FiHeart className="w-6 h-6" />
                    <div className="text-3xl font-bold">{stats.favoritesCount}</div>
                  </div>
                  <div className="text-gray-100">Favorites</div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
