import React, { useState, useEffect } from 'react';

const Profile = () => {
  const [stats, setStats] = useState({ moviesCount: 0, favoritesCount: 0 });
  const username = localStorage.getItem('username') || 'User';

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const response = await fetch('/api/movies/stats', {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`
          }
        });
        const data = await response.json();
        console.log('Stats received:', data);
        setStats(data);
      } catch (error) {
        console.error('Error fetching stats:', error);
      }
    };

    fetchStats();
  }, []);

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="max-w-4xl mx-auto">
        <div className="bg-gray-800 rounded-xl p-8 shadow-xl">
          <div className="flex items-center gap-6 mb-8">
            <img
              src={`https://ui-avatars.com/api/?name=${username}&background=random&size=128`}
              alt="Profile"
              className="w-32 h-32 rounded-full ring-4 ring-blue-500"
            />
            <div>
              <h1 className="text-3xl font-bold text-white mb-2">{username}</h1>
              <p className="text-gray-400">Movie Enthusiast</p>
            </div>
          </div>
          
          <div className="grid grid-cols-2 gap-6">
            <div className="bg-gray-700/50 rounded-lg p-6">
              <h3 className="text-lg font-medium text-gray-300 mb-2">Movies Added</h3>
              <p className="text-4xl font-bold text-blue-400">{stats.moviesCount}</p>
            </div>
            
            <div className="bg-gray-700/50 rounded-lg p-6">
              <h3 className="text-lg font-medium text-gray-300 mb-2">Favorites</h3>
              <p className="text-4xl font-bold text-purple-400">{stats.favoritesCount}</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
