import React from 'react';

const MovieCard = ({ movie, onDelete, onEdit }) => {
  return (
    <div className="bg-gray-800 rounded-lg shadow-lg overflow-hidden transition-transform hover:scale-105">
      <img
        src={movie.poster}
        alt={movie.title}
        className="w-full h-64 object-cover"
        onError={(e) => {
          e.target.src = 'https://via.placeholder.com/400x600?text=No+Image';
        }}
      />
      <div className="p-4">
        <h3 className="text-xl font-bold text-white mb-2">{movie.title}</h3>
        <p className="text-gray-400 mb-2">{movie.genre}</p>
        <p className="text-gray-400 mb-2">{movie.year}</p>
        <div className="flex items-center mb-4">
          <span className="text-yellow-500">â˜…</span>
          <span className="text-gray-400 ml-1">{movie.rating}/10</span>
        </div>
        <p className="text-gray-400 mb-4 line-clamp-2">{movie.description}</p>
        <div className="flex justify-between">
          <button
            onClick={() => onEdit(movie)}
            className="bg-blue-500 px-4 py-2 rounded hover:bg-blue-600 transition-colors"
          >
            Edit
          </button>
          <button
            onClick={() => onDelete(movie._id)}
            className="bg-red-500 px-4 py-2 rounded hover:bg-red-600 transition-colors"
          >
            Delete
          </button>
        </div>
      </div>
    </div>
  );
};

export default MovieCard;
