import React, { useState, useEffect } from 'react';
import { TextField, Button, Grid } from '@mui/material';

const MovieForm = ({ movie, onSubmit, onCancel }) => {
  const [formData, setFormData] = useState({
    title: movie?.title || '',
    description: movie?.description || '',
    imageUrl: movie?.imageUrl || '',
    genre: movie?.genre || '',
    year: movie?.year || '',
    rating: movie?.rating || ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevData => ({
      ...prevData,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(formData);
  };

  return (
    <form onSubmit={handleSubmit} className="p-6 bg-gray-800">
      <Grid container spacing={3}>
        <Grid item xs={12}>
          <TextField
            fullWidth
            label="Title"
            name="title"
            value={formData.title}
            onChange={handleChange}
            required
            variant="filled"
            className="bg-gray-700"
            sx={{
              input: { color: 'white' },
              label: { color: 'gray' }
            }}
          />
        </Grid>
        <Grid item xs={12}>
          <TextField
            fullWidth
            label="Description"
            name="description"
            multiline
            rows={4}
            value={formData.description}
            onChange={handleChange}
            required
            variant="filled"
            className="bg-gray-700"
            sx={{
              textarea: { color: 'white' },
              label: { color: 'gray' }
            }}
          />
        </Grid>
        <Grid item xs={12}>
          <TextField
            fullWidth
            label="Image URL"
            name="imageUrl"
            value={formData.imageUrl}
            onChange={handleChange}
            required
            variant="filled"
            className="bg-gray-700"
            sx={{
              input: { color: 'white' },
              label: { color: 'gray' }
            }}
          />
        </Grid>
        <Grid item xs={12} sm={6}>
          <TextField
            fullWidth
            label="Genre"
            name="genre"
            value={formData.genre}
            onChange={handleChange}
            required
            variant="filled"
            className="bg-gray-700"
            sx={{
              input: { color: 'white' },
              label: { color: 'gray' }
            }}
          />
        </Grid>
        <Grid item xs={12} sm={6}>
          <TextField
            fullWidth
            label="Year"
            name="year"
            type="number"
            value={formData.year}
            onChange={handleChange}
            required
            variant="filled"
            className="bg-gray-700"
            sx={{
              input: { color: 'white' },
              label: { color: 'gray' }
            }}
          />
        </Grid>
        <Grid item xs={12}>
          <div className="flex justify-end gap-4">
            <Button 
              onClick={onCancel} 
              variant="outlined"
              sx={{
                color: 'white',
                borderColor: 'white',
                '&:hover': {
                  borderColor: 'gray',
                  backgroundColor: 'rgba(255, 255, 255, 0.1)'
                }
              }}
            >
              Cancel
            </Button>
            <Button 
              type="submit" 
              variant="contained" 
              color="primary"
            >
              {movie ? 'Update Movie' : 'Add Movie'}
            </Button>
          </div>
        </Grid>
      </Grid>
    </form>
  );
};

export default MovieForm;
