import React, { memo } from 'react';
import { FiHeart, FiEdit2, FiTrash2, FiStar, FiMessageSquare } from 'react-icons/fi';

const MovieCard = memo(({ 
  movie, 
  onEdit, 
  onDelete, 
  onToggleFavorite, 
  onShowReviews,
  isFavorite, 
  showActions = true 
}) => {
  const defaultImage = 'https://via.placeholder.com/400x600?text=Movie+Poster';

  const handleEdit = (e) => {
    e.stopPropagation();
    onEdit(movie);
  };

  const handleDelete = (e) => {
    e.stopPropagation();
    onDelete(movie._id);
  };

  const handleFavorite = (e) => {
    e.stopPropagation();
    onToggleFavorite(movie._id);
  };

  const handleReviews = (e) => {
    e.stopPropagation();
    onShowReviews(movie._id);
  };

  return (
    <div className="bg-gray-800/50 rounded-xl overflow-hidden shadow-lg backdrop-blur-sm border border-gray-700/50 hover:shadow-blue-500/10 transition-all duration-300 hover:-translate-y-1">
      <div className="relative group">
        <img 
          src={movie.imageUrl || movie.poster || defaultImage} 
          alt={movie.title} 
          className="w-full h-64 object-cover transition-transform duration-300 group-hover:scale-105"
          onError={(e) => {
            e.target.onerror = null;
            e.target.src = defaultImage;
          }}
        />
        <div className="absolute inset-0 bg-gradient-to-t from-gray-900 via-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300" />
        
        {movie.rating && (
          <div className="absolute top-2 right-2 flex items-center gap-1 bg-black/60 px-2 py-1 rounded-full">
            <FiStar className="w-4 h-4 text-yellow-400 fill-current" />
            <span className="text-white text-sm">{movie.rating}</span>
          </div>
        )}
      </div>
      
      <div className="p-6">
        <h3 className="text-xl font-bold mb-2 text-white">{movie.title}</h3>
        <p className="text-gray-400 mb-4 line-clamp-2">{movie.description}</p>
        
        <div className="flex justify-between items-center">
          <div className="flex items-center gap-2 flex-wrap">
            <span className="px-3 py-1 bg-gray-700/50 rounded-full text-sm text-gray-300">
              {movie.genre}
            </span>
            {movie.year && (
              <span className="text-sm text-gray-500">
                {movie.year}
              </span>
            )}
          </div>
          
          <div className="flex items-center gap-2">
            {showActions && (
              <>
                <button
                  onClick={handleEdit}
                  className="text-blue-400 hover:text-blue-300 transition-colors duration-300"
                  title="Edit movie"
                >
                  <FiEdit2 className="w-5 h-5" />
                </button>
                <button
                  onClick={handleDelete}
                  className="text-red-400 hover:text-red-300 transition-colors duration-300"
                  title="Delete movie"
                >
                  <FiTrash2 className="w-5 h-5" />
                </button>
                <button
                  onClick={handleReviews}
                  className="text-purple-400 hover:text-purple-300 transition-colors duration-300"
                  title="Show reviews"
                >
                  <FiMessageSquare className="w-5 h-5" />
                </button>
              </>
            )}
            <button
              onClick={handleFavorite}
              className="text-red-400 hover:text-red-300 transition-colors duration-300"
              title={isFavorite ? "Remove from favorites" : "Add to favorites"}
            >
              <FiHeart className={`w-6 h-6 ${isFavorite ? 'fill-current' : ''}`} />
            </button>
          </div>
        </div>
      </div>
    </div>
  );
});

MovieCard.displayName = 'MovieCard';

export default MovieCard;
