import React, { useState, useEffect } from 'react';

const Profile = () => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchUserProfile = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await fetch('http://localhost:5000/api/users/me', {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });
       
        if (response.ok) {
          const userData = await response.json();
          setUser({
            ...userData,
            moviesCount: 0, // Will be updated when you implement movie counting
            favoritesCount: 0 // Will be updated when you implement favorites
          });
        }
      } catch (error) {
        console.error('Error fetching profile:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchUserProfile();
  }, []);

  return (
    <div className="min-h-screen bg-gray-900 text-white p-8">
      <div className="container mx-auto max-w-2xl">
        <div className="bg-gray-800 rounded-lg shadow-xl p-8">
          <div className="flex items-center gap-8 mb-8">
            <img
              src={`https://ui-avatars.com/api/?name=${user?.username}&size=128&background=random`}
              alt="Profile"
              className="w-32 h-32 rounded-full"
            />
            <div>
              <h1 className="text-3xl font-bold mb-2">{user?.username}</h1>
              <p className="text-gray-400">Member since {new Date(user?.createdAt).toLocaleDateString()}</p>
            </div>
          </div>

          <div className="space-y-6">
            <div className="border-t border-gray-700 pt-6">
              <h2 className="text-xl font-semibold mb-4">Profile Information</h2>
              <div className="space-y-4">
                <div>
                  <label className="text-gray-400 block mb-1">Username</label>
                  <div className="text-lg">{user?.username}</div>
                </div>
                <div>
                  <label className="text-gray-400 block mb-1">Email</label>
                  <div className="text-lg">{user?.email}</div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
