import React, { useState, useEffect } from 'react';

const Profile = () => {
  const [user, setUser] = useState({
    username: localStorage.getItem('username'),
    email: localStorage.getItem('email')
  });
  const [loading, setLoading] = useState(true);
  const [stats, setStats] = useState({ moviesCount: 0, favoritesCount: 0 });

  useEffect(() => {
    const fetchUserProfile = async () => {
      try {
        const token = localStorage.getItem('token');
        
        // Fetch user profile
        const userResponse = await fetch('http://localhost:5000/api/auth/me', {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        });

        // Fetch movies count
        const moviesResponse = await fetch('http://localhost:5000/api/movies/user/count', {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });

        // Fetch favorites count
        const favoritesResponse = await fetch('http://localhost:5000/api/favorites/count', {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });

        if (userResponse.ok) {
          const userData = await userResponse.json();
          const moviesData = await moviesResponse.json();
          const favoritesData = await favoritesResponse.json();

          setUser({
            username: userData.username || localStorage.getItem('username'),
            email: userData.email || localStorage.getItem('email')
          });

          setStats({
            moviesCount: moviesData.count || 0,
            favoritesCount: favoritesData.count || 0
          });
        }
      } catch (error) {
        console.error('Error fetching profile:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchUserProfile();
  }, []);

  const displayName = user.username || user.email.split('@')[0];

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-900 text-white flex items-center justify-center">
        <div className="text-xl">Loading profile...</div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-900 text-white p-8">
      <div className="container mx-auto max-w-2xl">
        <div className="bg-gray-800 rounded-lg shadow-xl p-8">
          <div className="flex items-center gap-8 mb-8">
            <img
              src={`https://ui-avatars.com/api/?name=${displayName}&size=128&background=random`}
              alt="Profile"
              className="w-32 h-32 rounded-full"
            />
            <div>
              <h1 className="text-3xl font-bold mb-2">{displayName}</h1>
              <p className="text-gray-400">Member since {new Date().toLocaleDateString()}</p>
            </div>
          </div>

          <div className="space-y-6">
            <div className="border-t border-gray-700 pt-6">
              <h2 className="text-xl font-semibold mb-4">Profile Information</h2>
              <div className="space-y-4">
                <div>
                  <label className="text-gray-400 block mb-1">Username</label>
                  <div className="text-lg">{displayName}</div>
                </div>
                <div>
                  <label className="text-gray-400 block mb-1">Email</label>
                  <div className="text-lg">{user.email}</div>
                </div>
              </div>
            </div>

            <div className="border-t border-gray-700 pt-6">
              <h2 className="text-xl font-semibold mb-4">Statistics</h2>
              <div className="grid grid-cols-2 gap-4">
                <div className="bg-gray-700 p-4 rounded-lg">
                  <div className="text-2xl font-bold">{stats.moviesCount}</div>
                  <div className="text-gray-400">Movies Added</div>
                </div>
                <div className="bg-gray-700 p-4 rounded-lg">
                  <div className="text-2xl font-bold">{stats.favoritesCount}</div>
                  <div className="text-gray-400">Favorites</div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
